  picapp_id = #{picappId,jdbcType=INTEGER},
  pic_adress = #{picAdress,jdbcType=VARCHAR},
  app_time = #{appTime,jdbcType=TIMESTAMP}
where pic_id = #{picId,jdbcType=INTEGER}

mapper.select:

select pic_id, user_id, picapp_id, pic_adress, app_time,state,awsome
from pic_answer
where pic_id = #{picId,jdbcType=INTEGER}

select pic_id, user_id, picapp_id, pic_adress, app_time,state,awsome
   from pic_answer
   where user_id = #{userId,jdbcType=VARCHAR} and picapp_id = #{picappId,jdbcType=VARCHAR}

select count(*)
   from pic_answer
   where picapp_id = #{picId,jdbcType=INTEGER}

select *
   from pic_answer
   where picapp_id = #{picappId,jdbcType=INTEGER} and user_id =#{userId,jdbcType=VARCHAR}

mapper.update:
update pic_answer
set awsome = #{awsome,jdbcType=INTEGER}
where pic_id = #{picId,jdbcType=INTEGER}

mapper.select:
select pic_id, user_id, picapp_id, pic_adress, app_time,state,awsome
   from pic_answer
   where state =0;

select count(pic_id)
    from pic_answer
    where state =0;

